module.exports = function(str, blank) {
    begin = str.indexOf('http');
    if (begin > -1) {
        i = begin;
        while (i<str.length && str[i]!==' ') {
            i++;
        }
        end = i;
        if (str[end-1].match(/[a-z]/i)) {
            url = str.substring(begin, end);
        } else {
            url = str.substring(begin, end-1);
        }
        if (blank) {
            new_str = str.replace(url, '<a target="_blank" rel="noreferrer" href="'+url+'">'+url+'</a>');
        } else {
            new_str = str.replace(url, '<a href="'+url+'">'+url+'</a>');
        }
        return new_str;
    } else {
        return str;
    }
}
